<?php\n// Assuming you have a mysqli connection established ($conn)\n// We'll use prepared statements to prevent SQL injection\n\n// Fetch categories\n$category_stmt = $conn->prepare("SELECT id, name FROM categories ORDER BY position ASC");\n$category_stmt->execute();\n$categories = $category_stmt->get_result();\n\n// Loop through categories and fetch assigned forums\nwhile ($category = $categories->fetch_assoc()) {\n    echo "<h2>{$category['name']}</h2>";\n    \n    // Prepare a statement to fetch forums for the current category\n    $forum_stmt = $conn->prepare("SELECT id, name, description FROM forums WHERE cat_id = ? ORDER BY position ASC");\n    $forum_stmt->bind_param("i", $category['id']);\n    $forum_stmt->execute();\n    $forums = $forum_stmt->get_result();\n    \n    // Loop through forums and display them\n    while ($forum = $forums->fetch_assoc()) {\n        echo "<p>{$forum['name']} - {$forum['description']}</p>";\n    }\n}