class A {\n    public $data;\n\n    public function __construct() {\n        $this->data = new Data();\n        $this->data->foo = 'bar';\n        $this->b = new B($this->data);\n    }\n}\n\nclass B {\n    private $data;\n\n    public function __construct(Data $data) {\n        $this->data = $data;\n    }\n\n    public function doSomething() {\n        echo $this->data->foo; // outputs "bar"\n    }\n}\n\nclass Data {\n    public $foo;\n}