// dbconnection.php\nclass DbConnection {\n    private $connection;\n\n    public function connectToDatabase() {\n        // Connect to database\n        $this->connection = new PDO('dsn', 'username', 'password');\n        return $this->connection;\n    }\n\n    public function closeDatabaseConnection() {\n        // Close database connection\n        $this->connection = null;\n    }\n}\n\n// dbdisplay.php\nclass DbDisplay {\n    private $dbConnection;\n\n    public function __construct(DbConnection $dbConnection) {\n        $this->dbConnection = $dbConnection;\n    }\n\n    public function displayPage($query) {\n        // Use the injected database connection\n        $connection = $this->dbConnection->connectToDatabase();\n        // Display page logic\n    }\n}\n\n// dbinsert.php\nclass DbInsert {\n    private $dbConnection;\n\n    public function __construct(DbConnection $dbConnection) {\n        $this->dbConnection = $dbConnection;\n    }\n\n    public function newPost($userCredentials, $postTextFromUser) {\n        // Use the injected database connection\n        $connection = $this->dbConnection->connectToDatabase();\n        // Insert post logic\n    }\n}\n\n// index.php\n$dbConnection = new DbConnection();\n$displayer = new DbDisplay($dbConnection);\n$displayer->displayPage($query);\n\n// userinsert.php\n$dbConnection = new DbConnection();\n$inserter = new DbInsert($dbConnection);\n$inserter->newPost($userCredentials, $postTextFromUser);