// Generate HTML\n$html = '';\nforeach ($imagePaths as $path) {\n    $html .= '<div class="image-container"><img src="combined_image.jpg" alt="' . basename($path) . '"></div>';\n}\n\n// Generate CSS\n$css = '';\n$x = 0;\nforeach ($imagePaths as $path) {\n    $imgInfo = getimagesize($path);\n    $css .= '.image-container:nth-child(' . ($x + 1) . ') { background-position: -' . $x . 'px 0; background-size: ' . $totalWidth . 'px ' . $totalHeight . 'px; }';\n    $x += $imgInfo[0];\n}\n\n// Output HTML and CSS\necho $html;\necho '<style>' . $css . '</style>';