// Assuming you have an array of image paths\n$imagePaths = array('image1.jpg', 'image2.jpg', 'image3.jpg');\n\n// Create a new image with the total width and height\n$totalWidth = 0;\n$totalHeight = 0;\nforeach ($imagePaths as $path) {\n    $imgInfo = getimagesize($path);\n    $totalWidth += $imgInfo[0];\n    $totalHeight = max($totalHeight, $imgInfo[1]);\n}\n$combinedImage = imagecreatetruecolor($totalWidth, $totalHeight);\n\n// Combine images\n$x = 0;\nforeach ($imagePaths as $path) {\n    $img = imagecreatefromjpeg($path);\n    imagecopy($combinedImage, $img, $x, 0, 0, 0, imagesx($img), imagesy($img));\n    $x += imagesx($img);\n    imagedestroy($img);\n}\n\n// Save the combined image\nimagejpeg($combinedImage, 'combined_image.jpg', 90);\n\n// Clean up\nimagedestroy($combinedImage);