$input = '2009/08/01/05';\n\n// Define the allowed formats\n$formats = array('Y/m', 'Y/m/d', 'Y/m/d/H', 'Y/m/d/H/i', 'Y/m/d/H/i/s');\n\n// Loop through the formats and try to parse the input\nforeach ($formats as $format) {\n    $dt = DateTime::createFromFormat($format, $input);\n    if ($dt !== false) {\n        // Validate the date ranges\n        if ($dt->format('Y') >= 1980 && $dt->format('Y') <= 2009) {\n            if ($dt->format('m') >= 1 && $dt->format('m') <= 12) {\n                // If day is not provided, set it to 1\n                if ($dt->format('d') == 0) {\n                    $dt->setDate($dt->format('Y'), $dt->format('m'), 1);\n                }\n                // If time is not provided, set it to 00:00:01\n                if ($dt->format('H') == 0) {\n                    $dt->setTime(0, 0, 1);\n                }\n                // Output the validated date\n                echo $dt->format('Y-m-d H:i:s');\n                break;\n            }\n        }\n    }\n}