class dbObjectIterator implements Iterator {\n    private $resultResource;\n    private $currentRow;\n\n    public function __construct($resultResource) {\n        $this->resultResource = $resultResource;\n    }\n\n    public function rewind() {\n        mysql_data_seek($this->resultResource, 0);\n    }\n\n    public function current() {\n        $this->currentRow = mysql_fetch_assoc($this->resultResource);\n        return $this->currentRow;\n    }\n\n    public function key() {\n        return mysql_num_rows($this->resultResource);\n    }\n\n    public function next() {\n        $this->currentRow = mysql_fetch_assoc($this->resultResource);\n    }\n\n    public function valid() {\n        return $this->currentRow !== false;\n    }\n}