function getNextAvailableTempFileName($directory, $baseName, $extension) {\n    $counter = 1;\n    do {\n        $fileName = sprintf("%s_%02d.%s", $baseName, $counter, $extension);\n        $filePath = $directory . DIRECTORY_SEPARATOR . $fileName;\n        $counter++;\n    } while (file_exists($filePath));\n    return $filePath;\n}\n\n// Example usage\n$directory = '/path/to/your/temp/directory'; // Make sure this path exists and is writable\n$baseName = 'tmp_file';\n$extension = 'jpg';\n\n$nextAvailableFile = getNextAvailableTempFileName($directory, $baseName, $extension);\necho $nextAvailableFile;