// Search for the promote class in theme files\n$files = scandir(get_template_directory());\nforeach ($files as $file) {\n    if (is_file(get_template_directory() . '/' . $file)) {\n        $content = file_get_contents(get_template_directory() . '/' . $file);\n        if (strpos($content, 'promote') !== false) {\n            echo "Found promote class in file: $file\n";\n        }\n    }\n}