$allowedAttributes = array('src', 'width', 'height');\n\n$html = $_POST['embed_code'];\n$doc = new DOMDocument();\n$doc->loadHTML($html);\n\n$embedTags = $doc->getElementsByTagName('embed');\nforeach ($embedTags as $tag) {\n    foreach ($tag->attributes as $attr) {\n        if (!in_array($attr->nodeName, $allowedAttributes)) {\n            $tag->removeAttribute($attr->nodeName);\n        }\n    }\n}\n\n$cleanHtml = $doc->saveHTML();