// OpenX API example\nrequire_once 'openx/api/ox3-api.php';\n\n$openx = new OpenX3_API();\n\n// Set API credentials\n$openx->setUsername('your_username');\n$openx->setPassword('your_password');\n\n// Retrieve banner statistics for a client\n$client_id = 123; // Replace with client ID\n$stats = $openx->getBannerStatistics($client_id);\n\n// Display banner statistics to client\nforeach ($stats as $stat) {\n    echo "Banner ID: " . $stat['banner_id'] . "\n";\n    echo "Impressions: " . $stat['impressions'] . "\n";\n    echo "Clicks: " . $stat['clicks'] . "\n";\n}