$currentDate = date('Y-m-d'); // initialize with current date\n\nif (isset($_GET['day'])) {\n    $day = $_GET['day'];\n    $currentDate = date('Y-m-d', strtotime($currentDate . ' ' . $day));\n}\n\nif (isset($_GET['last_monday'])) {\n    $currentDate = date('Y-m-d', strtotime('last monday'));\n}\n\n// display date\necho $currentDate;\n\n// your buttons\necho '<a href="?day=-1 day">-1 day</a>';\necho '<a href="?day=+1 day">+1 day</a>';\necho '<a href="?last_monday=1">Last Monday</a>';