class Product {\n    public $name;\n    public $price;\n    public $description;\n\n    function __construct($name, $price, $description) {\n        $this->name = $name;\n        $this->price = $price;\n        $this->description = $description;\n    }\n\n    function displayInfo() {\n        echo "Name: $this->name, Price: $this->price, Description: $this->description";\n    }\n}\n\n$phone = new Product("iPhone", 999, "A shiny new iPhone");\n$phone->displayInfo();