class Template {\n    protected $template_file;\n    protected $parent_template;\n\n    public function __construct($template_file) {\n        $this->template_file = $template_file;\n    }\n\n    public function extend($parent_template) {\n        $this->parent_template = new Template($parent_template);\n    }\n\n    public function render() {\n        if ($this->parent_template) {\n            $parent_output = $this->parent_template->render();\n            $output = file_get_contents($this->template_file);\n            $output = str_replace('{{ parent }}', $parent_output, $output);\n            return $output;\n        } else {\n            return file_get_contents($this->template_file);\n        }\n    }\n}