<?php\n// Ignore user aborts and allow the script\n// to run forever\nignore_user_abort(true);\nset_time_limit(0);\n\n$fp = fopen('loop.log', 'w');\nfwrite($fp, date('Y-m-d H:i:s') . ' Started' . PHP_EOL);\n\n// Consider using a more robust loop condition\n// and a try-catch block for error handling\nwhile (true) {\n    fwrite($fp, date('Y-m-d H:i:s') . ' Looped' . PHP_EOL);\n    if (file_exists('loop.stop')) {\n        break;\n    }\n    // Sleep for 100 seconds\n    sleep(100);\n}\n\nfwrite($fp, date('Y-m-d H:i:s') . ' Stopped' . PHP_EOL);\nfclose($fp);\n?>