$image1 = 'image1.jpg';\n$image2 = 'image2.jpg';\n\nif (isset($_GET['image'])) {\n  $image = $_GET['image'];\n  if ($image == 'image1') {\n    echo '<div id="image1"><img src="' . $image1 . '" alt="Image 1"></div>';\n  } elseif ($image == 'image2') {\n    echo '<div id="image2"><img src="' . $image2 . '" alt="Image 2"></div>';\n  }\n}\n\necho '<a href="?image=image1">Show Image 1</a>';\necho '<a href="?image=image2">Show Image 2</a>';\n\necho '<style>div:target { display: block; }</style>';