$query = "SELECT s.*, a.* FROM students s LEFT JOIN addresses a ON s.id = a.student_id";\n\n$result = mysqli_query($conn, $query);\n\nwhile ($row = mysqli_fetch_assoc($result)) {\n    $sortedArray = array(\n        "s" => array(\n            "id" => $row["s.id"],\n            "name" => $row["s.name"]\n        ),\n        "a" => array(\n            "id" => $row["a.id"],\n            "student_id" => $row["a.student_id"],\n            "address" => $row["a.address"]\n        )\n    );\n    print_r($sortedArray);\n}