abstract class Foo {\n    public function __get($name) {\n        echo "__get() called for $name\n";\n    }\n}\n\nclass Bar extends Foo {\n    private $var;\n\n    public function __construct() {\n        $this->var = 'private value';\n    }\n}\n\n$bar = new Bar();\n\n// Trying to access private $var from outside the class\necho $bar->var; // Fatal error: Uncaught Error: Cannot access private property Bar::$var