$url = 'https://example.com';\n$apiKey = 'YOUR_API_KEY';\n\n$ch = curl_init("https://phantomjscloud.com/api/v1/screenshot");\ncurl_setopt($ch, CURLOPT_RETURNTRANSFER, true);\ncurl_setopt($ch, CURLOPT_POST, true);\ncurl_setopt($ch, CURLOPT_POSTFIELDS, json_encode([\n    'url' => $url,\n    'width' => 1024,\n    'height' => 768,\n    'apiKey' => $apiKey,\n]));\n\n$response = curl_exec($ch);\ncurl_close($ch);\n\nif ($response !== false) {\n    $screenshot = json_decode($response, true);\n    echo "Screenshot generated successfully!";\n} else {\n    echo "Error generating screenshot!";\n}