use Firebase\JWT\JWT;\n\n// Set your secret key (store it securely!)\n$secretKey = 'your_secret_key_here';\n\n// User data to be authenticated\n$userData = array(\n    'username' => 'johnDoe',\n    'email' => 'john.doe@example.com'\n);\n\n// Generate JWT token\n$token = JWT::encode($userData, $secretKey, 'HS256');\n\n// Send the token to the second site\n\n// On the second site, verify the token\ntry {\n    $decoded = JWT::decode($token, $secretKey, array('HS256'));\n    // User is authenticated, proceed...\n} catch (Exception $e) {\n    // Invalid token, reject authentication\n}