$mysqli = new mysqli("your_host", "your_user", "your_password", "your_database");\n\n$query = "SELECT `code`, `datetime` FROM `fc` ORDER BY `datetime` DESC LIMIT 25";\n\n$stmt = $mysqli->prepare($query);\n$stmt->execute();\n$result = $stmt->get_result();\n\nwhile ($row = $result->fetch_assoc()) {\n    $code = htmlspecialchars($row["code"]);\n    $datetime = $row["datetime"];\n    $timeAgo = timeAgo($datetime);\n\n    $output .= "<li><a href=\"http://www.***=" . urlencode($code) . "\" target=\"_blank\">$code ($timeAgo)</a></li>";\n}\n\nfunction timeAgo($datetime) {\n    $currentTime = time();\n    $timeDiff = $currentTime - strtotime($datetime);\n\n    if ($timeDiff < 60) {\n        return $timeDiff . " seconds ago";\n    } elseif ($timeDiff < 3600) {\n        return floor($timeDiff / 60) . " minutes ago";\n    } elseif ($timeDiff < 86400) {\n        return floor($timeDiff / 3600) . " hours ago";\n    } else {\n        return floor($timeDiff / 86400) . " days ago";\n    }\n}