class MyClass {\n    public $publicProperty = 'public';\n    private $privateProperty = 'private';\n}\n\n$reflectionClass = new ReflectionClass('MyClass');\n$properties = $reflectionClass->getProperties(ReflectionProperty::IS_PUBLIC | ReflectionProperty::IS_PRIVATE);\n\nforeach ($properties as $property) {\n    $property->setAccessible(true); // Make private properties accessible\n    echo $property->getName() . ': ' . $property->getValue(new MyClass()) . "\n";\n}