use League\Uri\Uri;\n\n$uri = Uri::createFromUrl('https://example.com/path/to/resource');\necho $uri->getScheme(); // outputs "https"\necho $uri->getHost(); // outputs "example.com"\necho $uri->getPath(); // outputs "/path/to/resource"\n\n$uri->withPath('/new/path');\necho $uri->getPath(); // outputs "/new/path"\n\n$relativeUri = Uri::createFromUrl('/relative/path');\n$absoluteUri = $uri->resolve($relativeUri);\necho $absoluteUri->getPath(); // outputs "/path/to/relative/path"