class UserModel {\n    public function getName() {\n        return $this->name;\n    }\n}\n\nclass UserView {\n    public function display(UserModel $user) {\n        return "<h1>{$user->getName()}</h1>";\n    }\n}\n\nclass UserController {\n    public function handleRequest() {\n        $user = new UserModel();\n        $view = new UserView();\n        return $view->display($user);\n    }\n}