$dsn = 'mysql:host=your_host;dbname=your_db';\n$username = 'your_username';\n$password = 'your_password';\n\n$pdo = new PDO($dsn, $username, $password);\n\n// Set the timezone\n$pdo->exec("SET time_zone='America/Los_Angeles'");\n\n// Retrieve the TIMESTAMP field\n$stmt = $pdo->query("SELECT your_timestamp_field FROM your_table");\n$result = $stmt->fetch();\n\n// Display the TIMESTAMP field in the user's timezone\necho date('Y-m-d H:i:s', strtotime($result['your_timestamp_field']));