function generateCode($length = 8) {\n    $charset = 'ABCDEFGHJKLMNPQRSTUVWXYZ23456789';\n    $code = '';\n    for ($i = 0; $i < $length; $i++) {\n        $code .= $charset[random_int(0, strlen($charset) - 1)];\n    }\n    return $code;\n}\n\n// Generate a code and store it in the database\n$code = generateCode();\n$db = new PDO('mysql:host=your_host;dbname=your_db', 'your_user', 'your_pass');\n$stmt = $db->prepare('INSERT INTO codes (code) VALUES (:code)');\n$stmt->execute([':code' => $code]);