// MuseumController.php\nuse Gettext\Gettext;\n\nclass MuseumController extends Controller {\n    public function galleryGarden($lang) {\n        // Set the language\n        $gettext = new Gettext();\n        $gettext->setLanguage($lang);\n\n        // Translate the URL\n        $translatedUrl = $gettext->gettext('museum/gallery/garden');\n\n        // Render the view\n        return View::make('museum.gallery.garden', compact('translatedUrl'));\n    }\n}