function executeSqlFile($filename) {\n    $content = file_get_contents($filename);\n    $queries = array();\n    $currentQuery = '';\n\n    // Split the content, handling basic string literal semicolons\n    $inString = false;\n    for ($i = 0; $i < strlen($content); $i++) {\n        $char = $content[$i];\n        if ($char == '\'') {\n            $inString = !$inString;\n        }\n        if ($char == ';' && !$inString) {\n            $queries[] = trim($currentQuery);\n            $currentQuery = '';\n        } else {\n            $currentQuery .= $char;\n        }\n    }\n\n    // Add the last query if any\n    if (trim($currentQuery)) {\n        $queries[] = trim($currentQuery);\n    }\n\n    foreach ($queries as $query) {\n        if (trim($query)) { // Ignore empty queries\n            mysql_query($query);\n        }\n    }\n}\n\nexecuteSqlFile('dump.sql');