// Loop 1: Posts from category 1 with attachments and meta data\n$query1 = new WP_Query(array(\n  'post_type' => 'post',\n  'cat' => 1,\n  'posts_per_page' => 5,\n  'meta_key' => 'my_meta_key',\n  'meta_value' => 'my_meta_value'\n));\n\nif ($query1->have_posts()) {\n  while ($query1->have_posts()) {\n    $query1->the_post();\n    the_title();\n    the_content();\n    echo get_post_meta(get_the_ID(), 'my_meta_key', true);\n    echo wp_get_attachment_url(get_post_thumbnail_id());\n  }\n  wp_reset_postdata();\n}\n\n// Loop 2: Posts from category 2 with custom fields and taxonomy terms\n$query2 = new WP_Query(array(\n  'post_type' => 'post',\n  'cat' => 2,\n  'posts_per_page' => 5,\n  'tax_query' => array(\n    array(\n      'taxonomy' => 'my_taxonomy',\n      'field' => 'slug',\n      'terms' => 'my_term'\n    )\n  )\n));\n\nif ($query2->have_posts()) {\n  while ($query2->have_posts()) {\n    $query2->the_post();\n    the_title();\n    the_content();\n    echo get_post_meta(get_the_ID(), 'my_custom_field', true);\n    echo get_the_term_list(get_the_ID(), 'my_taxonomy', '', ', ');\n  }\n  wp_reset_postdata();\n}