// LanguageMiddleware.php\nuse Gettext\Gettext;\n\nclass LanguageMiddleware {\n    public function handle($request, Closure $next) {\n        // Get the language from the request\n        $lang = $request->input('lang');\n\n        // Set the language\n        $gettext = new Gettext();\n        $gettext->setLanguage($lang);\n\n        // Translate the URL\n        $translatedUrl = $gettext->gettext($request->path());\n\n        // Set the translated URL as a request attribute\n        $request->attributes->set('translatedUrl', $translatedUrl);\n\n        // Call the next middleware\n        return $next($request);\n    }\n}