function schoolDD($name, $selected){\n   $select = '';\n   if( $selected != null ){\n      $select = $selected;\n   }\n\n   // Note: The order of SQL clauses is important. \n   // WHERE should come before ORDER BY\n   $qry = "SELECT *  \n           FROM   school\n           WHERE display = 'Y'\n           ORDER BY name, id";\n\n   $schools = _execQry($qry);\n\n   $html = '<select name="'.$name.'" >';\n\n   foreach( $schools as $s ){\n      $html .= '<option value="'. htmlspecialchars($s['id']) .'"';\n      if( $select == $s['name'] ){\n         $html .= ' selected="selected"'; // space before selected\n      }\n      $html .= '>'. htmlspecialchars($s['name']) . '</option>';\n   }\n   $html .= '</select>';\n   return $html;\n}