// Assuming you have already fetched the data and the rows and columns from the database\n// Let's say $data is your string from the database, and $rows and $columns are the user inputs\n\n$data = "d1,d2,d3,d4,d5,d6,d7,d8,d9"; // Example data\n$rows = 3;\n$columns = 3;\n\n// Explode the data into an array\n$dataArray = explode(",", $data);\n\n// Check if the data can be displayed in the requested format\nif (count($dataArray) != $rows * $columns) {\n    echo "Data cannot be displayed in the requested format.";\n} else {\n    // Initialize a counter\n    $counter = 0;\n\n    // Loop through the rows\n    for ($i = 0; $i < $rows; $i++) {\n        // Start the row\n        echo "|";\n\n        // Loop through the columns\n        for ($j = 0; $j < $columns; $j++) {\n            // Display the data\n            echo "___" . $dataArray[$counter] . "__|";\n\n            // Increment the counter\n            $counter++;\n        }\n\n        // End the row\n        echo "\n";\n    }\n}