<?php\nheader('Content-type: text/xml');\n\n$dbhost = 'localhost';\n$dbname = 'links';\n$dbuser = 'root';\n$dbpass = 'password'; // Consider using a more secure method of storing your password\n\n// Connect to database using mysqli\n$conn = new mysqli($dbhost, $dbuser, $dbpass, $dbname);\n\nif ($conn->connect_error) {\n    die('Connect error: ' . $conn->connect_error);\n}\n\n// Check for errors after executing query\n$sql = 'SELECT * FROM news LIMIT 5';\n$result = $conn->query($sql);\n\nif (!$result) {\n    die('Query error: ' . $conn->error);\n}\n\n// Use full open tags instead of short open tags\n?>\n<rss version="2.0">\n<channel>\n    <title> </title>\n    <description> </description>\n    <link></link>\n<?php\nwhile ($row = $result->fetch_assoc()) {\n?>\n    <item>\n        <title><?php echo $row['title']; ?></title>\n        <author><?php echo $row['author']; ?></author>\n        <link>http://MYSITE.com/news.php?id=<?php echo $row['id']; ?></link>\n    </item>\n<?php\n}\n?>\n</channel>\n</rss>