$stmt = $pdo->prepare('\n    SELECT * FROM blog \n    WHERE (blog_title LIKE :term1 OR blog_content LIKE :term1) \n       OR (blog_title LIKE :term2 OR blog_content LIKE :term2) \n       OR (blog_title LIKE :term3 OR blog_content LIKE :term3) \n    ORDER BY blog_title\n');\n\n$stmt->execute([\n    ':term1' => '%' . $searchTerm1 . '%',\n    ':term2' => '%' . $searchTerm2 . '%',\n    ':term3' => '%' . $searchTerm3 . '%',\n]);