function search_quotes($search_term) {\n  $db = new mysqli('your_host', 'your_user', 'your_pass', 'your_db');\n\n  // Escape the search term to prevent SQL injection\n  $search_term = $db->real_escape_string($search_term);\n\n  // Use a full-text search query to retrieve the quotes\n  $query = "SELECT id, quote, MATCH (quote) AGAINST ('$search_term' IN BOOLEAN MODE) AS relevance\n            FROM quotes\n            WHERE MATCH (quote) AGAINST ('$search_term' IN BOOLEAN MODE)\n            ORDER BY relevance DESC";\n\n  $results = $db->query($query);\n\n  // Fetch the results and return them as an array\n  $quotes = array();\n  while ($row = $results->fetch_assoc()) {\n    $quotes[] = array(\n      'id' => $row['id'],\n      'quote' => $row['quote'],\n      'relevance' => $row['relevance']\n    );\n  }\n\n  return $quotes;\n}