function get_newest_tabs() {\n  $db_open;\n  $sql = "\n    SELECT \n      t.tab_id, \n      t.tab_version, \n      t.number_of_hits, \n      a.artist_name, \n      u.user_alias, \n      s.song_name, \n      t.time_added\n    FROM \n      tabs t\n    JOIN \n      users u ON t.user_id = u.user_id\n    JOIN \n      songs s ON t.song_id = s.song_id\n    JOIN \n      artist a ON s.artist_id = a.artist_id\n    ORDER BY \n      t.time_added DESC\n    LIMIT 15\n  ";\n  $result = mysql_query($sql) or die("ERROR - newest tabs function: ".mysql_error());\n\n  $tabs = array();\n  while ($row = mysql_fetch_assoc($result)) {\n    $tabs[] = $row;\n  }\n  return $tabs;\n}