<?php\n\n// Ensure you have the correct path to the Zend Framework library\nrequire_once 'Zend/Search/Lucene.php';\n\n// Create a new Lucene index or open an existing one\n$index = Zend_Search_Lucene::create('/path/to/your/index');\n\n// Specify the directory where your view files are located\n$viewDirectory = '/path/to/your/views/';\n\n// Iterate through each view file\nforeach (scandir($viewDirectory) as $file) {\n    if ($file !== '.' && $file !== '..') {\n        $filePath = $viewDirectory . $file;\n        \n        // Extract the content from the view file\n        $content = file_get_contents($filePath);\n        \n        // Create a new Lucene document\n        $doc = new Zend_Search_Lucene_Document();\n        \n        // Add the file name as the title and the content as the body\n        $doc->addField(Zend_Search_Lucene_Field::text('title', $file));\n        $doc->addField(Zend_Search_Lucene_Field::unStored('body', $content));\n        \n        // Add the document to the index\n        $index->addDocument($doc);\n    }\n}\n\n// Commit the changes to the index\n$index->commit();\n\n?>