// Set a test cookie\nsetcookie('test_cookie', '1', time() + 3600);\n\n// Redirect to the same page to check if the cookie is set\nheader('Location: ' . $_SERVER['PHP_SELF']);\nexit;\n\n// Check if the cookie is set\nif (!isset($_COOKIE['test_cookie'])) {\n    // Cookies are not enabled, redirect to a page with instructions\n    header('Location: cookies_not_enabled.php');\n    exit;\n} else {\n    // Cookies are enabled, delete the test cookie\n    setcookie('test_cookie', '', time() - 3600);\n}