$start = microtime(true);\n\nfor ($i = 1010374999; $i < 1421374999; $i++) {\n    $square = $i * $i;\n    $digits = array();\n    while ($square > 0) {\n        $digits[] = $square % 10;\n        $square = (int) ($square / 10);\n    }\n    $digits = array_reverse($digits);\n    if ($digits[0] == 1 && $digits[2] == 2 && $digits[4] == 3 && $digits[6] == 4 && $digits[8] == 5 && $digits[10] == 6 && $digits[12] == 7 && $digits[14] == 8 && $digits[16] == 9 && $digits[18] == 0) {\n        echo $i;\n        break;\n    }\n}\n\n$end = microtime(true);\n$elapsed = $end - $start;\necho "<br />The time to calculate was $elapsed seconds";