class ClassA {\n    public static function call() {\n        $class = get_called_class();\n        return call_user_func(array($class, 'inner'));\n    }\n\n    public static function inner() {\n        return "Class A";\n    }\n}\n\nclass ClassB extends ClassA {\n    public static function inner() {\n        return "Class B";\n    }\n}\n\necho "<p>Class A = " . ClassA::call();\necho "<p>Class B = " . ClassB::call();