class Controller_Dispatcher extends Zend_Controller_Dispatcher_Standard {\n    public function loadClass($className) {\n        $className = str_replace('_', '/', $className);\n        $file = $this->getControllerDirectory() . '/' . $className . '.php';\n        if (!file_exists($file)) {\n            throw new Zend_Controller_Dispatcher_Exception('Controller file not found: ' . $file);\n        }\n        require_once $file;\n        return $className;\n    }\n\n    public function classToFilename($class) {\n        return str_replace('_', '/', $class) . '.php';\n    }\n}