<?php\n// assuming you have a mysql query to fetch the data\n$query = mysql_query("SELECT * FROM your_table");\nwhile ($row = mysql_fetch_assoc($query)) {\n  $id = $row['id'];\n  echo '<tr id="'.$id.'">';\n  // your table data here\n  echo '</tr>';\n  echo '<div id="remove_'.$id.'" style="display:none">';\n  echo '<a href="#">Remove</a>';\n  echo '</div>';\n}\n?>\n\n<script>\n$(document).ready(function(){\n  $('tr').hover(function(){\n    var id = $(this).attr('id');\n    $('#remove_'+id).show();\n  }, function(){\n    var id = $(this).attr('id');\n    $('#remove_'+id).hide();\n  });\n});\n</script>