// application/helpers/Menu.php\n\nclass Menu {\n    public static function get_menu_items() {\n        return array(\n            'home' => array('label' => 'Home', 'uri' => '/'),\n            'products' => array('label' => 'Products', 'uri' => 'products'),\n            'support' => array('label' => 'Support', 'uri' => 'support'),\n            'contact' => array('label' => 'Contact', 'uri' => 'contact'),\n        );\n    }\n\n    public static function get_active_item($uri) {\n        $menu_items = self::get_menu_items();\n        foreach ($menu_items as $item) {\n            if ($item['uri'] == $uri) {\n                return $item;\n            }\n        }\n        return null;\n    }\n}