function getChildren($parentId, $data) {\n    $children = [];\n    $queue = [$parentId];\n    while ($queue) {\n        $currentId = array_shift($queue);\n        foreach ($data as $node) {\n            if ($node['parent_id'] == $currentId) {\n                $children[] = $node['id'];\n                $queue[] = $node['id'];\n            }\n        }\n    }\n    return $children;\n}