function xml2array($node) {\n    $arrayData = array();\n    if ($node->hasChildNodes()) {\n        $subNodes = $node->childNodes;\n        foreach ($subNodes as $subNode) {\n            if ($subNode->nodeType == 1) { // Only consider element nodes (nodeType == 1)\n                if ($subNode->hasChildNodes() && ($subNode->childNodes->length > 1 || $subNode->childNodes->item(0)->nodeType != 3)) {\n                    // If the node has child nodes that are not just a single text node, recurse\n                    $arrayData[$subNode->nodeName] = xml2array($subNode);\n                } else {\n                    // If the node only contains a single text node or no child nodes, just take the nodeValue\n                    $arrayData[$subNode->nodeName] = $subNode->nodeValue;\n                }\n            }\n        }\n    }\n    return $arrayData;\n}\n\n$data = 'path/to/xmldoc.xml';\n$xmlDoc = new DOMDocument();\n$xmlDoc->load($data);\n$xmlRoot = $xmlDoc->documentElement; // Use documentElement to get the root\n\n$xmlArray = xml2array($xmlRoot);\n\n// Printing the output\nprint_r($xmlArray);