function getChildren($parentId, $data) {\n    $children = [];\n    foreach ($data as $node) {\n        if ($node['parent_id'] == $parentId) {\n            $children[] = $node['id'];\n            $children = array_merge($children, getChildren($node['id'], $data));\n        }\n    }\n    return $children;\n}\n\n$data = [\n    ['id' => 0, 'parent_id' => 7],\n    ['id' => 0, 'parent_id' => 4],\n    ['id' => 4, 'parent_id' => 9],\n    ['id' => 4, 'parent_id' => 14],\n    ['id' => 4, 'parent_id' => 16],\n    ['id' => 9, 'parent_id' => 6],\n];\n\n$parentId = 4;\n$children = getChildren($parentId, $data);\nprint_r($children);