// Include the PHPBB2 configuration file\ninclude('./forums/config.php');\n\n// Start the PHPBB2 session\n$phpbb_root_path = './forums/';\nrequire($phpbb_root_path . 'extension.inc');\nrequire($phpbb_root_path . 'includes/session.php');\nstart_session();\n\n// Check if the user is logged in\nif ($userdata['session_logged_in']) {\n    // Get the user's group membership\n    $sql = "SELECT group_id FROM " . USERS_TABLE . " WHERE user_id = " . $userdata['user_id'];\n    $result = $db->sql_query($sql);\n    $group_id = $db->sql_fetchfield('group_id', false, $result);\n\n    // Check if the user belongs to a specific group\n    if ($group_id == YOUR_GROUP_ID) {\n        // Grant access to the restricted pages\n        // Your code here...\n    } else {\n        // Deny access\n        header('Location: ./forums/index.php');\n        exit;\n    }\n} else {\n    // User is not logged in, redirect to the forum login page\n    header('Location: ./forums/login.php');\n    exit;\n}