$stmt = $mysqli->prepare("SELECT category, cat_id FROM categories");\n$stmt->execute();\n$result = $stmt->get_result();\n\nwhile ($row = $result->fetch_assoc()) {\n    $cat[] = $row['category'];\n    $cat_id[] = $row['cat_id'];\n}\n\n$stmt->close();\n\nforeach ($cat_id as $i => $id) {\n    $checked = in_array($id, $link_cat) ? 'checked="checked"' : '';\n    echo '<li><input type="checkbox" ' . $checked . ' name="' . $id . '" />' . $cat[$i] . '</li>';\n}