$allowedMimeTypes = [\n    'pdf' => 'application/pdf',\n    'doc' => 'application/msword',\n    'docx' => 'application/vnd.openxmlformats-officedocument.wordprocessingml.document',\n    'xls' => 'application/vnd.ms-excel',\n    'xlsx' => 'application/vnd.openxmlformats-officedocument.spreadsheetml.sheet',\n    'txt' => 'text/plain',\n];\n\n$uploadedFile = $_FILES['file'];\n\nif (is_uploaded_file($uploadedFile['tmp_name'])) {\n    $finfo = finfo_open(FILEINFO_MIME_TYPE);\n    $fileMimeType = finfo_file($finfo, $uploadedFile['tmp_name']);\n    finfo_close($finfo);\n\n    if (in_array($fileMimeType, $allowedMimeTypes)) {\n        // File type is valid, proceed with upload\n        move_uploaded_file($uploadedFile['tmp_name'], 'uploads/' . $uploadedFile['name']);\n    } else {\n        // File type is not allowed, reject upload\n        echo 'Invalid file type';\n    }\n} else {\n    // File was not uploaded properly, reject upload\n    echo 'Invalid file upload';\n}