<?php\n\n// Set the secret code for the cookie\n$secret_code = 'my_secret_code';\n\n// Function to verify the cookie value\nfunction verify_cookie($cookie_value, $ip_address) {\n  global $secret_code;\n  return $cookie_value === hash('sha256', $ip_address . $secret_code);\n}\n\n// Check if the cookie is set and valid\nif (isset($_COOKIE['auth']) && verify_cookie($_COOKIE['auth'], $_SERVER['REMOTE_ADDR'])) {\n  // Display the protected content\n  echo 'Welcome, authenticated user!';\n} else {\n  // Redirect to the login page\n  header('Location: login.php');\n  exit;\n}\n\n?>